const Rule = require('./Rule');

module.exports = class Day extends Rule {
  defaultMessage = '{{field}} is not a valid day.';

  check(value) {
    if (!value) {
      return true;
    }
    if (typeof value !== 'string') {
      return false;
    }
    const [DAY, MONTH, YEAR] = this.component.dayFirst ? [0, 1, 2] : [1, 0, 2];
    const values = value.split('/').map(x => parseInt(x, 10)),
      day = values[DAY],
      month = values[MONTH],
      year = values[YEAR],
      maxDay = getDaysInMonthCount(month, year);

    if (isNaN(day) || day < 0 || day > maxDay) {
      return false;
    }
    if (isNaN(month) || month < 0 || month > 12) {
      return false;
    }
    if (isNaN(year) || year < 0 || year > 9999) {
      return false;
    }
    return true;

    function isLeapYear(year) {
      // Year is leap if it is evenly divisible by 400 or evenly divisible by 4 and not evenly divisible by 100.
      return !(year % 400) || (!!(year % 100) && !(year % 4));
    }

    function getDaysInMonthCount(month, year) {
      switch (month) {
        case 1:     // January
        case 3:     // March
        case 5:     // May
        case 7:     // July
        case 8:     // August
        case 10:    // October
        case 12:    // December
          return 31;
        case 4:     // April
        case 6:     // June
        case 9:     // September
        case 11:    // November
          return 30;
        case 2:     // February
          return isLeapYear(year) ? 29 : 28;
        default:
          return 31;
      }
    }
  }
};

import NativePromise from 'native-promise-only';
const dropbox = (formio) => ({
  uploadFile(file, fileName, dir, progressCallback) {
    return new NativePromise(((resolve, reject) => {
      // Send the file with data.
      const xhr = new XMLHttpRequest();

      if (typeof progressCallback === 'function') {
        xhr.upload.onprogress = progressCallback;
      }

      const fd = new FormData();
      fd.append('name', fileName);
      fd.append('dir', dir);
      fd.append('file', file);

      // Fire on network error.
      xhr.onerror = (err) => {
        err.networkError = true;
        reject(err);
      };

      xhr.onload = () => {
        if (xhr.status >= 200 && xhr.status < 300) {
          const response = JSON.parse(xhr.response);
          response.storage = 'dropbox';
          response.size = file.size;
          response.type = file.type;
          response.url = response.path_lower;
          resolve(response);
        }
        else {
          reject(xhr.response || 'Unable to upload file');
        }
      };

      xhr.onabort = reject;

      xhr.open('POST', `${formio.formUrl}/storage/dropbox`);
      const token = formio.getToken();
      if (token) {
        xhr.setRequestHeader('x-jwt-token', token);
      }
      xhr.send(fd);
    }));
  },
  downloadFile(file) {
    const token = formio.getToken();
    file.url =
      `${formio.formUrl}/storage/dropbox?path_lower=${file.path_lower}${token ? `&x-jwt-token=${token}` : ''}`;
    return NativePromise.resolve(file);
  }
});

